mapper.select:
SELECT a.id, a.site_id AS 'siteId', a.column_id AS 'columnId', c.column_name AS 'columnName', c.column_flag AS 'columnFlag', d.content, a.title, a.title_color AS 'titleColor', a.link, a.image_url AS imageUrl, a.image_name AS imageName, a.keywords, a.description, a.weight, a.weight_time AS 'weightTime', a.hits, a.sort, a.posid AS 'posId', a.`status`, a.create_by AS 'createBy', a.create_time AS 'createTime', a.update_by AS 'updateBy', a.update_time AS 'updateTime', a.remark FROM cms_article a JOIN cms_column c ON a.column_id = c.column_id JOIN cms_article_data d ON d.article_id = a.id
AND a.id = #{id}
AND a.site_id = #{siteId}
AND a.column_id = #{columnId}
AND c.column_flag = #{columnFlag}
AND a.`status` = #{status}
AND a.title like concat('%', #{title}, '%')
ORDER BY a.sort ASC, a.update_time DESC

mapper.insert:
insert into cms_article_data (
	id,
	article_id,
	content
)values(
	#{id},
	#{articleId},
	#{content}
)

mapper.update:
update cms_article
column_id = #{columnId}, title = #{title}, image_url = #{imageUrl}, image_name = #{imageName}, description = #{description}, sort = #{sort}, remark = #{remark}, `status` = #{status}, update_by = #{updateBy}, update_time = sysdate()
where id = #{id}

mapper.select:
SELECT a.id, a.site_id AS 'siteId', a.column_id AS 'columnId', a.title, a.title_color AS 'titleColor', a.link, a.image_url AS 'imageUrl', a.image_name AS 'imageName', a.keywords, a.description, a.weight, a.weight_time AS 'weightTime', a.hits, a.sort, a.posid AS 'posId', a.`status`, a.create_by AS 'createBy', a.create_time AS 'createTime', a.update_by AS 'updateBy', a.update_time AS 'updateTime', a.remark FROM cms_article a
AND a.site_id IN
#{siteId}
