mapper.select:
SELECT t.column_id AS id, t.parent_id AS pId, t.site_id AS siteId, s.name AS siteName, t.column_code AS columnCode, t.column_name AS name,
( SELECT y.column_name FROM cms_column y WHERE y.column_id IN ( SELECT u.parent_id FROM cms_column u WHERE u.column_id =#{columnId} ) ) AS upColumnName,
t.column_name AS title, t.column_flag AS columnFlag, t.`status`, t.is_show AS display, t.out_link AS outLink, t.href, t.create_by AS createBy, t.create_time AS createTime, t.update_by AS updateby, t.update_time AS updateTime, t.sort, t.remark FROM cms_column t LEFT JOIN cms_site s ON s.id = t.site_id
AND t.parent_id =#{parentId}
AND t. STATUS =#{status}
AND t.is_show = #{display}
AND t.site_id = #{siteId}
AND s.name like concat('%', #{siteName}, '%')
AND t.column_id =#{columnId}
AND t.out_link =#{outLink}
AND t.column_name like concat('%', #{columnName}, '%')
ORDER BY t.sort ASC
