mapper.select:
SELECT
FROM oa_leave a
WHERE 1=1 AND a.del_flag ='0'
AND u.user_id LIKE '%'||#{userId}||'%' '%'+#{userId}+'%' CONCAT('%', #{userId}, '%')
AND a.id=#{id}
AND a.user_id=#{userId}
AND d.dept_id=#{deptId}
AND u.user_name like concat('%', #{name}, '%')
ORDER BY a.update_date DESC

mapper.update:
UPDATE oa_leave SET 
	hr_text = #{hrText},
	update_by = #{updateBy.userId}, 
	update_date = #{updateDate}
WHERE id = #{id}

UPDATE oa_leave SET 
	dept_text = #{deptText},
	update_by = #{updateBy.userId}, 
	update_date = #{updateDate}
WHERE id = #{id}

UPDATE oa_leave SET 
	zjl_text = #{zjlText},
	update_by = #{updateBy.userId}, 
	update_date = #{updateDate}
WHERE id = #{id}
