mapper.select:
SELECT a.id, a.notify_type AS 'notifyType', a.notify_title AS 'notifyTitle', a.notify_content AS 'notifyContent', a.files, a.`status`, a.create_by AS 'createBy', a.create_time AS 'createTime', a.update_by AS 'updateBy', a.update_time AS 'updateTime', a.remark, a.del_flag AS 'delFlag', b.read_num AS 'readNum', b.un_read_num AS 'unReadNum'
,r.read_flag AS 'readFlag'
FROM oa_notify a LEFT JOIN ( SELECT r.notify_id, sum( CASE WHEN r.read_flag = '1' THEN 1 ELSE 0 END ) read_num, sum( CASE WHEN r.read_flag != '1' THEN 1 ELSE 0 END ) un_read_num FROM oa_notify_record r GROUP BY r.notify_id ) b ON b.notify_id = a.id
JOIN oa_notify_record r ON r.notify_id = a.id AND r.user_id = #{userId}
a.del_flag = 0
AND a.id = #{id}
AND a.notify_title like concat('%', #{notifyTitle}, '%')
AND a.notify_type = #{notifyType}
AND a.`status` = #{status}
ORDER BY a.update_time DESC
